import React from 'react';
import './App.css';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import HomeIcon from '@material-ui/icons/Home';
import FavoriteIcon from '@material-ui/icons/Favorite';
import VideocamIcon from '@material-ui/icons/Videocam';
import TwitterIcon from '@material-ui/icons/Twitter';
import FacebookIcon from '@material-ui/icons/Facebook';
import InstagramIcon from '@material-ui/icons/Instagram';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';

function App() {

  function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
      <div
        role="tabpanel"
        hidden={value !== index}
        id={`tabpanel-${index}`}
        {...other}
      >
        {value === index && (
          <Box p={3}>
            <Typography>{children}</Typography>
          </Box>
        )}
      </div>
    );
  }

  const [value, setValue] = React.useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const aSections = [
    {
      label: "Home Page",
      icon: <HomeIcon />,
      id: "home",
      contents: <p>Home Page!</p>
    },
    {
      label: "About Me",
      icon: <FavoriteIcon />,
      id: "about",
      contents: <p>About Me!</p>
    },
    {
      label: "Stream Info",
      icon: <VideocamIcon />,
      id: "stream",
      contents: <p>Stream Info!</p>
    }
  ];

  return (
    <div className="App">
      <header className="page-header">
        <img src={"https://static-cdn.jtvnw.net/jtv_user_pictures/690c1f7a-dda3-424c-9520-9b12f4ce58b6-profile_image-70x70.png"} className="profile-icon" alt="Whoops..." />
        <p>
          KrunkyXO
        </p>
      </header>
      <AppBar className="app-bar-wrapper" position="static">
        <Tabs value={value} onChange={handleChange}  className="tab-bar">
          {aSections.map((tab) => (
            <Tab icon={tab.icon} label={tab.label} id={tab.id} />
          ))}
        </Tabs>
      </AppBar>
      {aSections.map((tab, index) => (
        <TabPanel value={value} index={index}>
          {tab.contents}
        </TabPanel>
      ))}
      <AppBar className="app-bar-wrapper footer" position="fixed">
        <ButtonGroup color="inherit" variant="text">
          {/* <Button href="https://www.twitch.tv/krunkyxo">Twitch</Button> */}
          <Button href="https://www.instagram.com/jaydead.xo/"><InstagramIcon className="margin-right-2"/><span>Instagram</span></Button>
          <Button href="https://twitter.com/ludicol0"><TwitterIcon className="margin-right-2"/>Twitter</Button>
          <Button href="https://www.facebook.com/jaydeadxo"><FacebookIcon className="margin-right-2"/>Facebook</Button>
        </ButtonGroup>
      </AppBar>
    </div>
  );
}

export default App;
